import React, { useEffect, useState } from 'react'
import { Link, useParams } from 'react-router-dom'
import Navbar from './Navbar'
import TrendingSlider from './TredingSlider'

const Category = () => {
    const { name } = useParams();
    const [data, setData] = useState([])

    useEffect(() => {
      const fetchData = async () => {
        const api = await fetch(`https://www.themealdb.com/api/json/v1/1/filter.php?a=${name}`)
        const data = await api.json();
        setData(data.meals)
      }
      fetchData();
    }, [name])

  return (
    <>
      <Navbar />
      <div style={{
        width: '90%',
        margin: 'auto',
        display: 'grid',
        gridTemplateColumns: 'repeat(auto-fit, minmax(15rem, 1fr))',
        gridGap: '1rem',
        marginTop: '2rem'
      }}>
        {
          data.map((d) => {
            return (
              <Link to={`/${d.idMeal}`} className='link' key={d.idMeal}>
                <div style={{ textAlign: 'center' }}>
                  <div className="img">
                    <img
                      src={d.strMealThumb}
                      alt={d.strMeal}
                      className="transition-all duration-300 hover-blink"
                      style={{ width: '13rem' }}
                    />
                  </div>
                  <h3>{d.strMeal}</h3>
                </div>
              </Link>
            )
          })
        }
      </div>

      <TrendingSlider />
    </>
  )
}

export default Category;
