import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';

const Navbar = () => {
    const navigate = useNavigate();
    const [input, setInput] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
        navigate(`/search/${input}`);
    };

    return (
        <div className="flex h-screen bg-gray-100">
            {/* Sidebar */}
            <div className="w-64 bg-gray-800 text-white flex flex-col py-4">
                <Link to="/" className="text-center text-2xl font-bold mb-6">
                    Recipe Sharing App
                </Link>
                <nav className="flex-grow">
                    <Link to="/category/indian" className="block py-2 px-4 hover:bg-gray-700">
                        Indian
                    </Link>
                    <Link to="/category/american" className="block py-2 px-4 hover:bg-gray-700">
                        American
                    </Link>
                    <Link to="/category/british" className="block py-2 px-4 hover:bg-gray-700">
                        British
                    </Link>
                    <Link to="/category/chinese" className="block py-2 px-4 hover:bg-gray-700">
                        Chinese
                    </Link>
                    <Link to="/category/thai" className="block py-2 px-4 hover:bg-gray-700">
                        Thai
                    </Link>
                </nav>
                <form onSubmit={handleSubmit} className="px-4 mt-4">
                    <input
                        type="text"
                        placeholder="Search..."
                        value={input}
                        onChange={(e) => setInput(e.target.value)}
                        className="w-full py-2 px-3 rounded-md bg-gray-700 text-white focus:outline-none focus:ring-2 focus:ring-blue-500"
                    />
                </form>
            </div>

            {/* Main Content */}
            <div className="flex-grow p-6">
                <h1 className="text-2xl font-bold">Main Content Area</h1>
                {/* Add your main content here */}
            </div>
        </div>
    );
};

export default Navbar;
