import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';

const Navbar = () => {
    const navigate = useNavigate();
    const [input, setInput] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
        navigate(`/search/${input}`);
    };

    return (
        <div className="bg-gray-800 text-white fixed top-0 left-0 w-full z-50 pt-">
            {/* Navbar Container */}
            <div className="container mx-auto flex items-center justify-between py-4 px-6">
                {/* Logo */}
                <Link to="/" className="text-2xl font-bold">
                    Recipe Sharing App
                </Link>

                {/* Navigation Links */}
                <div className="flex space-x-6">
                    <Link to="/category/indian" className="hover:text-gray-300">
                        Indian
                    </Link>
                    <Link to="/category/american" className="hover:text-gray-300">
                        American
                    </Link>
                    <Link to="/category/british" className="hover:text-gray-300">
                        British
                    </Link>
                    <Link to="/category/chinese" className="hover:text-gray-300">
                        Chinese
                    </Link>
                    <Link to="/category/thai" className="hover:text-gray-300">
                        Thai
                    </Link>
                </div>

                {/* Search Bar */}
                <form onSubmit={handleSubmit} className="flex items-center">
                    <input
                        type="text"
                        placeholder="Search..."
                        value={input}
                        onChange={(e) => setInput(e.target.value)}
                        className="py-2 px-3 rounded-md bg-gray-700 text-white focus:outline-none focus:ring-2 focus:ring-blue-500"
                    />
                </form>
            </div>
        </div>
    );
};

export default Navbar;
